#define int16 signed short#define int32 signed intint16 filtreZ11f( int16 u, int16* xn){    int16 y;        // output    int32 Acc;        // accumulator    // intermediate variables    Acc = xn[0];    int16 T0    = Acc;    Acc = xn[1];    int16 T1    = Acc;    Acc = xn[2];    int16 T2    = Acc;    Acc = xn[3];    int16 T3    = Acc;    // states    Acc = (T0    * -17601);    Acc = Acc + (T1    << 13);    Acc = Acc + ((xn[0] * 32684) >> 1);    Acc = Acc + ((u     * 19126) >> 2);    xn[0] = Acc >> 14;    Acc = (T0    * -18065);    Acc = Acc + (T2    << 13);    Acc = Acc + ((xn[1] * 27098) >> 2);    Acc = Acc + ((u     * -20652) >> 3);    xn[1] = Acc >> 14;    Acc = (T0    * -25826);    Acc = Acc + (T3    << 12);    Acc = Acc + ((xn[2] * 32324) >> 1);    Acc = Acc + ((u     * 30193) >> 5);    xn[2] = Acc >> 14;    Acc = (T0    * -30395);    Acc = Acc + (xn[3] * 32554);    Acc = Acc + ((u     * 16982) >> 4);    xn[3] = Acc >> 15;    // output(s)    Acc = T0   ;    y     = Acc;    // output    return y;}