#define int16 signed short#define int32 signed intint16 filtreZ1f( int16 u, int16** xn, int16** xnp){    int16 y;        // output    int32 Acc;        // accumulator    // intermediate variables    Acc = ((*xn)[3] * -23022);    Acc = Acc + (u        << 13);    (*xnp)[0] = Acc >> 15;    Acc = ((*xn)[0] << 13);    Acc = Acc + ((*xn)[3] * 31606);    (*xnp)[1] = Acc >> 15;    Acc = ((*xn)[1] << 13);    Acc = Acc + ((*xn)[3] * -17745);    (*xnp)[2] = Acc >> 14;    Acc = ((*xn)[2] << 14);    Acc = Acc + ((*xn)[3] * 18978);    (*xnp)[3] = Acc >> 13;    // output(s)    Acc = (((*xn)[0] * 19126) >> 2);    Acc = Acc + (((*xn)[1] * -26529) >> 2);    Acc = Acc + ((*xn)[2] * -22452);    Acc = Acc + (((*xn)[3] * -27229) >> 2);    y         = Acc >> 12;    //permutations    int16* temp = (*xn);    (*xn) = (*xnp);    (*xnp) = temp;    // output    return y;}