%Purpose:
% Fixed-point algorithm in Matlab language
% (it uses integer to simulate fixed-point)
%
%Syntax:
% y = funR4(u)
%
%Parameters:
% y: filtered output(s)
% u: intput(s)
%
% date: 05-May-2011 15:14:52
% Automatically generated by implementMATLAB / FWRToolbox


function y = funR4(u)

% initialize                   
u = round(2.^6.*u);y = zeros( size(u,1), 1 );xn = zeros(2,1);xnp = zeros(2,1);                
                               
for i=1:size(u,1)

    % intermediate variables    Acc00 = xn(1) * 106;    Acc00 = Acc00 + xn(2) * 38;    Acc00 = Acc00 + u(i)  * -4;    xnp(1) = floor( Acc00/2^7 );    Acc01 = xn(1) * -23;    Acc01 = Acc01 + xn(2) * 105;    Acc01 = Acc01 + u(i)  * -31;    xnp(2) = floor( Acc01/2^7 );    % output(s)    Acc02 = xn(1) * -122;    Acc02 = Acc02 + xn(2) * -24;    Acc02 = Acc02 + u(i)  * 3;    y(i)   = floor( Acc02/2^7 );    %permutations    xn = xnp;
    
end

y = 2.^-6.*y;